 Verse.create!(content: "وَأَنَّ إِلَىٰ رَبِّكَ الْمُنْتَهَىٰ", number: 42, chapter_id: 53)
 Verse.create!(content: "وَأَنَّهُ هُوَ أَضْحَكَ وَأَبْكَىٰ", number: 43, chapter_id: 53)
