 Verse.create!(content: "وَثَمُودَ فَمَا أَبْقَىٰ", number: 51, chapter_id: 53)
 Verse.create!(content: "وَقَوْمَ نُوحٍ مِنْ قَبْلُ ۖ إِنَّهُمْ كَانُوا هُمْ أَظْلَمَ وَأَطْغَىٰ", number: 52, chapter_id: 53)
 Verse.create!(content: "وَالْمُؤْتَفِكَةَ أَهْوَىٰ", number: 53, chapter_id: 53)
