 Verse.create!(content: "فَبِأَيِّ آلَاءِ رَبِّكَ تَتَمَارَىٰ", number: 55, chapter_id: 53)
 Verse.create!(content: "هَٰذَا نَذِيرٌ مِنَ النُّذُرِ الْأُولَىٰ", number: 56, chapter_id: 53)
 Verse.create!(content: "أَزِفَتِ الْآزِفَةُ", number: 57, chapter_id: 53)
 Verse.create!(content: "لَيْسَ لَهَا مِنْ دُونِ اللَّهِ كَاشِفَةٌ", number: 58, chapter_id: 53)
 Verse.create!(content: "أَفَمِنْ هَٰذَا الْحَدِيثِ تَعْجَبُونَ", number: 59, chapter_id: 53)
 Verse.create!(content: "وَتَضْحَكُونَ وَلَا تَبْكُونَ", number: 60, chapter_id: 53)
