 Verse.create!(content: "أَمْ لَمْ يُنَبَّأْ بِمَا فِي صُحُفِ مُوسَىٰ", number: 36, chapter_id: 53)
 Verse.create!(content: "وَإِبْرَاهِيمَ الَّذِي وَفَّىٰ", number: 37, chapter_id: 53)
 Verse.create!(content: "أَلَّا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ", number: 38, chapter_id: 53)
 Verse.create!(content: "وَأَنْ لَيْسَ لِلْإِنْسَانِ إِلَّا مَا سَعَىٰ", number: 39, chapter_id: 53)
