 Verse.create!(content: "مِنْ نُطْفَةٍ إِذَا تُمْنَىٰ", number: 46, chapter_id: 53)
 Verse.create!(content: "وَأَنَّ عَلَيْهِ النَّشْأَةَ الْأُخْرَىٰ", number: 47, chapter_id: 53)
 Verse.create!(content: "وَأَنَّهُ هُوَ أَغْنَىٰ وَأَقْنَىٰ", number: 48, chapter_id: 53)
 Verse.create!(content: "وَأَنَّهُ هُوَ رَبُّ الشِّعْرَىٰ", number: 49, chapter_id: 53)
